package appendonly

import (
	"encoding/base64"
	"testing"
	"time"

	crypto "github.com/libp2p/go-libp2p-crypto"
	"github.com/stretchr/testify/require"
)

func TestRecordSign(t *testing.T) {
	want := struct {
		Signature []byte
		Hash      string
	}{
		Signature: []byte{0x38, 0x9e, 0x4e, 0xf2, 0x22, 0xe1, 0x71, 0x6c, 0x48, 0xdb, 0x14, 0x42, 0xbe, 0xfe, 0xec, 0x6c, 0xd4, 0x35, 0x4d, 0x74, 0x14, 0x89, 0xd8, 0x43, 0x37, 0xa8, 0xf3, 0x82, 0xac, 0xb8, 0x5, 0xe6, 0x32, 0x91, 0xd2, 0x28, 0x66, 0xf9, 0xed, 0x2, 0xc, 0x42, 0x9f, 0x17, 0x96, 0x8c, 0xbd, 0x35, 0x5f, 0x47, 0xe6, 0xf6, 0x52, 0x63, 0x23, 0xc5, 0x4a, 0x11, 0x4d, 0x69, 0x18, 0x98, 0x9f, 0x1a, 0x68, 0x57, 0x67, 0xd2, 0x2b, 0xd0, 0x2a, 0x92, 0x22, 0x5b, 0x6b, 0xd2, 0x46, 0x25, 0x5b, 0xab, 0x6, 0x2c, 0x45, 0x95, 0x52, 0x95, 0xda, 0x4, 0xd8, 0x10, 0xb0, 0x4d, 0x9d, 0xec, 0x26, 0xa, 0x48, 0x91, 0xec, 0xe9, 0x5e, 0x9c, 0x89, 0xd2, 0xcf, 0x91, 0xc1, 0x34, 0x7a, 0x48, 0xcc, 0x8f, 0x67, 0x62, 0x42, 0xd4, 0x45, 0x9c, 0x1e, 0x20, 0x8b, 0x13, 0xee, 0x14, 0x41, 0x35, 0x32, 0x7b, 0x4f, 0x97, 0x53, 0x84, 0x7c, 0x69, 0x35, 0xad, 0x60, 0x87, 0x62, 0x70, 0x6a, 0x0, 0xe9, 0x21, 0xfd, 0x20, 0xbc, 0xf3, 0xac, 0x90, 0xcd, 0x9c, 0xe6, 0x23, 0x6, 0xaf, 0x52, 0xc9, 0x20, 0xe5, 0xa5, 0x89, 0x37, 0x44, 0xa0, 0xd7, 0x48, 0x85, 0x50, 0xa1, 0x3b, 0x8f, 0x84, 0x8c, 0x5d, 0xe7, 0xca, 0xca, 0xc0, 0xc, 0xef, 0xa5, 0x59, 0x85, 0x2e, 0xf5, 0xe2, 0x45, 0x95, 0xaa, 0x5a, 0x86, 0x66, 0x29, 0x9d, 0x9b, 0xf1, 0x9d, 0x48, 0x67, 0xae, 0x1c, 0x77, 0x15, 0x70, 0x47, 0x70, 0xe0, 0xb, 0xe0, 0x79, 0xcf, 0x5c, 0xcb, 0xd, 0xaf, 0x28, 0xd, 0xd6, 0x97, 0x26, 0x68, 0xdb, 0x4e, 0x3b, 0x38, 0xc4, 0xa5, 0x7d, 0xed, 0x6a, 0xdd, 0xf8, 0x3f, 0x39, 0x13, 0xbd, 0xf4, 0x47, 0x75, 0x24, 0x3e, 0x33, 0xbf, 0x55, 0x79, 0x8f, 0xb9, 0xcf, 0xc, 0x79, 0xbc, 0x35, 0x6, 0x26, 0x94},
		Hash:      "1220e29bfa8d1003b78d7571be6805db0a6f09b54d01b3d5aab8422db5ec3863054a",
	}

	rec := Record{
		Seq:           0,
		Author:        "author",
		MultihashCode: defaultMultihashCode,
		Previous:      "",
		AppendTime:    time.Date(2020, 1, 1, 0, 0, 0, 0, time.UTC),
	}

	key := testPrivKey(t)

	signed, err := rec.Sign(key)
	require.NoError(t, err)

	require.Equal(t, want.Signature, signed.Signature, "signature doesn't match")
	require.Equal(t, want.Hash, signed.Hash(), "signed record must have hash")
	err = signed.Verify(key.GetPublic())
	require.NoError(t, err, "signature verification failed")

	// === Test Marshaling ===

	raw, err := EncodeSignedRecord(signed)
	require.NoError(t, err)

	read, err := DecodeSignedRecord(raw)
	require.NoError(t, err)

	require.Equal(t, want.Signature, read.Signature, "read signature doesn't match")
	require.Equal(t, want.Hash, read.Hash(), "read hash doesn't match")
	err = read.Verify(key.GetPublic())
	require.NoError(t, err, "read signature verification failed")
}

func testPrivKey(t *testing.T) crypto.PrivKey {
	t.Helper()
	testPk := ``
	data, err := base64.StdEncoding.DecodeString(testPk)
	require.NoError(t, err)

	pk, err := crypto.UnmarshalPrivateKey(data)
	require.NoError(t, err)

	return pk
}
